    // Procura a ID passada no banco de dados
    private void ButtonBuscarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ButtonBuscarActionPerformed
        ConexoesMySQL conec = new ConexoesMySQL();
        Professor ObjProfessor = conec.BuscarProfessorNoMySQL(TID.getText());
        
        if(ObjProfessor != null){
            TNome.setText(ObjProfessor.nome);
            TCPF.setText(Long.toString(ObjProfessor.cpf));
            TDataNascimento.setText(ObjProfessor.dataNascimento);
            TContato.setText(ObjProfessor.contato);
            TDataAdmissao.setText(ObjProfessor.getDataAdmissao());
            TSalarioBruto.setText(Double.toString(ObjProfessor.getSalarioBruto()));
            
        }
        else{
            JOptionPane.showMessageDialog(null, "Professor Não Encontrado", "Busca", INFORMATION_MESSAGE);
        }    // TODO add your handling code here:
    }//GEN-LAST:event_ButtonBuscarActionPerformed

