    // Atualiza o cadastro do professor no banco de dados
    private void ButtonSalvarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ButtonSalvarActionPerformed
        ConexoesMySQL conec = new ConexoesMySQL();
        Professor prof = new Professor();

        prof.setDados(TNome.getText(),
                      TDataNascimento.getText(),
                      Long.parseLong(TCPF.getText()),
                      TContato.getText(),
                      Integer.parseInt(TID.getText()),
                      TDataAdmissao.getText(),
                      Double.parseDouble(TSalarioBruto.getText()));
        
        conec.atualizarDadosProfessorNoMySQL(prof);
        
        TID.setText("");
        TNome.setText("");
        TCPF.setText("");
        TDataNascimento.setText("");
        TContato.setText("");
        TDataAdmissao.setText("");
        TSalarioBruto.setText("");
        
    }//GEN-LAST:event_ButtonSalvarActionPerformed

